mapper.select:
select 

from antifake_cipher
where company_code = #{cipher.companyCode,jdbcType=VARCHAR} and product_code = #{cipher.productCode,jdbcType=VARCHAR} and code = #{cipher.code,jdbcType=VARCHAR}

	select 
  
  from antifake_cipher
  
  	
  		and company_id = #{companyId}
  	
  	
  		and company_code = #{companyCode}
  	
  	
  		and product_code = #{productCode}
  	
  	
  		and batch = #{batch}
  	
  
  order by #{orderBy} desc 
  limit #{pageNum}, #{pageSize}
